import cl from './Dialogs.module.css';
import {NavLink} from "react-router-dom";

const DialogItem = (props) => {

    const path = '/DIALOGS/' + props.id

    return <div className={cl.item}>
        <NavLink to={path} className={onActive}>{props.name}</NavLink></div>

}

const DialogMessage = (props) => {

    return <div className={cl.message} id={props.id}>{props.message}</div>
}

const onActive = ({isActive}) => isActive ? `${cl.active}` : ''

export const Dialogs = (props) => {



    const users = props.usersData.map((user) => <DialogItem name={user.name} id={user.id}/>)
    const messages = props.messagesData.map((message) => <DialogMessage id={message.id} message={message.message}/>)

    return (
        <div className={cl.dialogs}>
            <div className={cl.dialogsItems}>
                {users}
            </div>

            <div className={cl.messages}>
                {messages}
            </div>
        </div>
    )
}
