import React, {useState} from "react";
import classes from './Post.module.css';

export const posts = [
    {name: 'Oldos', messagePost: 'Hi! Like my post!', count: 1},
    {name: 'Ariel', messagePost: 'Like my post!', count: 15},
    {name: 'Inga', messagePost: 'Hi! Like my!', count: 22},
    {name: 'Mara', messagePost: 'Like!', count: 2},
]

export const Post = (props) => {

    const [count, setCount] = useState(props.count)

    const likesCounter = () => {
        setCount(count + 1)
    }
    return (
        <div className={classes.postContainer}>
            <img className={classes.avaPosts}
                 src={'https://cdn.fishki.net/upload/post/2016/07/28/2026065/tn/2f3228627f3c87813ef0712c0f5325ec.jpg'}
            />
            <a>{props.name} </a>
            <div className={classes.item}>{props.messagePost}</div>
            <button className={'like'}
                    onClick={likesCounter}>{props.count}</button>
        </div>
    )
}
