import React, { useRef, FC } from "react";
import cl from './Dialogs.module.css';
import { DialogItem } from "./Dialog";
import { Message } from "./Message/Message";
import { DialogPageType } from "../../../redux/state";

interface DialogsProps {
    state: DialogPageType;
}

export const Dialogs: FC<DialogsProps> = (props) => {
    const newMessageElement = useRef<HTMLTextAreaElement>(null);

    const sendNewMessage = () => {
        if (newMessageElement.current) {
            const text = newMessageElement.current.value;
            alert(text);
            newMessageElement.current.value = ''; // Очистка поля после отправки сообщения
        }
    };

    const users = props.state.usersData.map(user => (
        <DialogItem name={user.name} id={user.id} key={user.id} />
    ));

    const messages = props.state.messagesData.map(message => (
        <Message id={message.id} message={message.message} key={message.id} />
    ));

    return (
        <div className={cl.dialogs}>
            <div className={cl.dialogsItems}>
                {users}
            </div>
            <div className={cl.messages}>
                {messages}
                <textarea className={'inputTextarea'} ref={newMessageElement} />
                <div>
                    <button onClick={sendNewMessage}>Send</button>
                </div>
            </div>
        </div>
    );
};
