import React, {useState} from "react";
import './MyPosts.module.css'
import {Post} from "./Post";

let classes = {'myPosts': 'MyPorts_myPosts__6Q9c6'}


export const MyPosts = () => {


    const [count, setCount] = useState(posts.likes)

    const likesCounter = () => {
        setCount(count + 1)
    }


const posts = [
    {name: 'Oldos', messagePost: 'Hi! Like my post!', likes: 1},
    {name: 'Ariel', messagePost: 'Like my post!', likes: 15},
    {name: 'Inga', messagePost: 'Hi! Like my!', likes: 22},
    {name: 'Mara', messagePost: 'Like!', likes: 2},
]



    return <div className={classes.myPosts}>
        <div>
            <textarea className={'inputTextarea'}/>
        </div>
        <div>
            <button>Add Post</button>
        </div>
        <div className={classes.item}>
            <h3>My posts</h3>

            {posts.map((p => {
                return <Post name={p.name} messagePost={p.messagePost} count={p.likes}/>
            }))}
            <button className={'like'}
                    onClick={likesCounter}>like{`${count}`}</button>
        </div>
    </div>
}
