import React, {useRef, useState} from "react";
import './MyPosts.module.css'
import {Post} from "./Post";
import state, {PostType} from "../../../redux/state";
import {AppProps} from "../../../App";


let classes = { 'myPosts': 'MyPorts_myPosts__6Q9c6', 'item': 'MyPorts_item__6Q9c6' }

// type MyPostsProps = {
//     posts: PostType[];
//     removePost: (id: string) => void;
// }

export const MyPosts = (props: AppProps) => {
    const [myPosts, setMyPosts] = useState<PostType[]>(state.profilePage.posts);
    const removePost = (id: string) => setMyPosts(myPosts.filter(post => post.id !== id))
    // const [posts, setPosts] = useState(state.profilePage.posts);

    let newPostElement = useRef<HTMLTextAreaElement>(null);


    const addPost = () => {
        if (newPostElement.current) {
            console.log(newPostElement.current.value);
        }
    }


    return <div className={classes.myPosts}>
        <div>
            <textarea ref={newPostElement} className={'inputTextarea'} onChange={()=>{}} />
        </div>
        <div>
            <button onClick={addPost}>Add Post</button>
        </div>
        <div className={classes.item}>
            <h3>My posts</h3>

                {myPosts.map((p => {

                    return (
                           <Post id={p.id} messagePost={p.messagePost} count={p.count}  />
                    )
                }))}
        </div>
    </div>
}
