import React, { useState } from 'react';
import classes from './Post.module.css';

type PostProps = {
    id: string;
    name: string;
    messagePost: string;
    count: number;
    removePost: (id: string) => void;
};

const Post: React.FC<PostProps> = ({ id, name, messagePost, count, removePost }) => {
    const [postCount, setPostCount] = useState(count);

    const likesCounter = () => {
        setPostCount(postCount + 1);
    };

    return (
        <div className={classes.postContainer}>
            <img
                className={classes.avaPosts}
                src={'https://cdn.fishki.net/upload/post/2016/07/28/2026065/tn/2f3228627f3c87813ef0712c0f5325ec.jpg'}
                alt="Avatar"
            />
            <a>{name}</a>
            <div className={classes.item}>{messagePost}</div>
            <button className={'like'} onClick={likesCounter}>{postCount}</button>
            <button onClick={() => removePost(id)}>Remove Post</button>
        </div>
    );
};

export default Post;
