import './Post.css'
import {useState} from "react";

// export type MyPostType ={
//     name: string
//     messagePost: string
// }

export const Post = (props) => {

    const [count, setCount] = useState(0)

    const addLike = () => {
        setCount(count + 1)
    }


    return (
        <div className="postContainer">
            <img className={'avaPosts'}
                 src={'https://st2.depositphotos.com/1009868/45177/i/450/depositphotos_451772690-stock-photo-tambov-russian-federation-january-2021.jpg'}
            />
            <a>{props.name} </a>
            <span className={'item'}>{props.messagePost}</span>
            <button className={'like'}
                    onClick={addLike}>like {count}</button>
        </div>
    )
}
