import React, {useState} from "react";
import './MyPosts.module.css'
import {Post} from "./Post";
import state from "../../../redux/state";
import {v1} from "uuid";

let classes = {'myPosts': 'MyPorts_myPosts__6Q9c6'}

export const MyPosts = (props) => {

const [newMessagePost, setNewMessagePost] = useState("");

    const addPost = state.profilePage.posts
// const newPost = {id: v1(), name: 'Oldos', messagePost: newMessagePost, count: 0}

    // const [post, setPost] = useState()
    // const [name, setName] = useState('')

const addNewPost = () => {
        let text = document.getElementById("new-post").value;
        alert(text)
}

    const onChangeInput = () => {
        setNewMessagePost()
    }

    return <div className={classes.myPosts}>
        <div>
            <textarea id={'new-post'} className={'inputTextarea'} onChange={onChangeInput}/>
        </div>
        <div>
            <button onClick={addPost}>Add Post</button>
        </div>
        <div className={classes.item}>
            <h3>My posts</h3>

            {addPost.map((p => {

                return (
                    <Post name={p.name} messagePost={p.messagePost} count={p.count} key={p.id}/>
                )

            }))}

        </div>
    </div>
}
