import {NavLink, useMatch} from "react-router-dom";
import cl from "./Navbar/NavBar.module.css";

export const CustomLink = ({children, to, ...props}) => {
    const match = useMatch(to)
    return <NavLink
        to={to}
        style={{color: match ? `${cl.active}` : ''}} {...props}
    >
        {children}
    </NavLink>

}
